import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import "./Movie.css";
import axios from "axios";

interface Movie {
  poster?: string;
  title?: string;
  crawl?: string;
  date?: string;
  id?: number;
  producer?: string;
  director?: string;
  characters?: [];
}

const Movie: React.FC<Movie> = ({
  poster,
  title,
  crawl,
  date,
  id,
  producer,
  director,
}) => {
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState(false);

  return (
    <div className="movie__card">
      <img
        src={poster}
        alt="Movie Poster"
        onClick={() => {
          {
            navigate(`/${id}`);
            setIsVisible(!isVisible);
          }
        }}
      />
      <div className="movie__description">
        <p>{title}</p>
        <span>{date}</span>
        <p> {crawl}</p>
        <div
          className={isVisible ? "movie__details" : "movie__details__hidden"}
        >
          <>
            <span>Producer:</span>
            <p>{producer}</p>
            <span>Director:</span>
            <p>{director}</p>
          </>
        </div>
      </div>
    </div>
  );
};

export default Movie;
