import { useSelector } from "react-redux";
import { getAllMovies } from "../redux/movies/movieSlice";
import { useParams } from "react-router-dom";
import Movie from "../components/Movie";
// import Movie from "../components/Movie";

// interface Movies {
//   movie: string;
//   episode_id: number;
// }

// interface Id {
//   moviesId: number;
// }

const MovieDetail = () => {
  // const movies = useSelector((state: any) =>
  //   console.log("Logging in movieDetail", state.movies)
  // );
  const id = useParams();
  const result = useSelector(getAllMovies);
  console.log("Result is ", result);
  console.log(id.moviesId);

  return (
    <div>
      {result.map((movie: any, idx: any) => {
        return id.moviesId == movie.episode_id ? (
          <Movie
            poster={movie.img}
            title={movie.title}
            crawl={movie.opening_crawl}
            date={movie.release_date}
          />
        ) : null;
      })}
    </div>
  );
};

export default MovieDetail;
