import { useNavigate } from "react-router-dom";
import { useState } from "react";
import "./Movie.css";

interface Movie {
  poster?: string;
  title?: string;
  crawl?: string;
  date?: string;
  id?: number;
}

const Movie: React.FC<Movie> = ({ poster, title, crawl, date, id }) => {
  const [isVisible, setIsVisible] = useState<boolean>();
  const navigate = useNavigate();

  return (
    <div className="movie__card">
      <img
        src={poster}
        alt="Movie Poster"
        onClick={() => {
          navigate(`/${id}`);
        }}
      />
      <div className="movie__description">
        <p>{title}</p>
        <span>{date}</span>
        <p> {crawl}</p>
        <div
          className={isVisible ? "movie__details" : "movie__details__hidden"}
        >
          <p>{title}</p>
          <p>{id}</p>
        </div>
      </div>
    </div>
  );
};

export default Movie;
