import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import "./Movie.css";
import axios from "axios";

interface Movie {
  poster?: string;
  title?: string;
  crawl?: string;
  date?: string;
  id?: number;
  producer?: string;
  director?: string;
  characters?: [];
}

const Movie: React.FC<Movie> = ({
  poster,
  title,
  crawl,
  date,
  id,
  producer,
  director,
  characters,
}) => {
  const [isVisible, setIsVisible] = useState<boolean>(true);
  const [movieCharacters, setMovieCharacters] = useState<any>([]);

  const navigate = useNavigate();

  const fetchCharacters = (characters: any) => {
    characters?.map((character: any, idx: any) => {
      axios.get(character).then((response) => {
        const { name } = response.data;
        console.log(name);
        setMovieCharacters([...movieCharacters, response.data.name]);
      });
    });
  };
  useEffect(() => {
    fetchCharacters(characters);
  }, []);

  return (
    <div className="movie__card">
      <img
        src={poster}
        alt="Movie Poster"
        onClick={() => {
          navigate(`/${id}`);
        }}
      />
      <div className="movie__description">
        <p>{title}</p>
        <span>{date}</span>
        <p> {crawl}</p>
        <div
          className={isVisible ? "movie__details" : "movie__details__hidden"}
        >
          <>
            <p>{producer}</p>
            <p>{director}</p>
            {movieCharacters.map((character: string, idx: any) => (
              <p>{character}</p>
            ))}
          </>
        </div>
      </div>
    </div>
  );
};

export default Movie;
