import { useState, useEffect } from "react";
import axios from "axios";
import Movie from "../components/Movie";
import { useDispatch } from "react-redux";
import { addMovies } from "../redux/movies/movieSlice";

interface Movies {
  title: string;
  opening_crawl: string;
  release_date: string;
  img?: string;
  episode_id: number;
}

const Home = () => {
  const dispatch = useDispatch();
  const [movies, setMovies] = useState<any>([]);
  const [favorites, setFavorites] = useState<any>([]);

  useEffect(() => {
    axios.get("/movies").then((response) => {
      dispatch(addMovies(response.data));
      setFavorites(movies[0]);
      return setMovies(response.data);
    });
  }, []);

  useEffect(() => {
    localStorage.setItem("favorites", JSON.stringify(favorites));
  }, [favorites]);

  return (
    <div className="App">
      <button className="add__button">Show Favorites</button>
      {movies.map((movie: Movies, idx: number) => {
        return (
          <div key={idx}>
            <Movie
              poster={movie.img}
              title={movie.title}
              crawl={movie.opening_crawl}
              date={movie.release_date}
              id={movie.episode_id}
            />
          </div>
        );
      })}
    </div>
  );
};

export default Home;
