import { useNavigate } from "react-router-dom";
import { useState } from "react";
import "./Movie.css";

interface Movie {
  poster?: string;
  title?: string;
  crawl?: string;
  date?: string;
  id?: number;
  producer?: string;
  director?: string;
  characters?: [];
}

const Movie: React.FC<Movie> = ({
  poster,
  title,
  crawl,
  date,
  id,
  producer,
  director,
}) => {
  const navigate = useNavigate();

  const [favoriteMovies, setFavoriteMovies] = useState();

  // const addToFavorites = () => {
  //   localStorage.setItem(favoriteMovies);
  // };

  console.log(favoriteMovies);

  return (
    <div className="movie__card">
      <img
        src={poster}
        alt="Movie Poster"
        onClick={() => {
          navigate(`/${id}`);
        }}
      />
      <div className="movie__description">
        <p>{title}</p>
        <span>{date}</span>
        <p> {crawl}</p>
        <button onClick={() => setFavoriteMovies(poster, title, crawl, date)}>
          Add to favorites
        </button>
        <div className={producer ? "movie__details" : "movie__details__hidden"}>
          <>
            <span>Producer:</span>
            <p>{producer}</p>
            <span>Director:</span>
            <p>{director}</p>
          </>
        </div>
      </div>
    </div>
  );
};

export default Movie;
