import { useState, useEffect } from "react";
import axios from "axios";
import { useSelector } from "react-redux";
import { getAllMovies } from "../redux/movies/movieSlice";
import { useParams } from "react-router-dom";
import Movie from "../components/Movie";

const MovieDetail = () => {
  const id = useParams();
  const result = useSelector(getAllMovies);
  const [movieCharacters, setMovieCharacters] = useState<object[]>([]);

  const links = result.map((response: any) => {
    return response.characters;
  });

  console.log(links);
  return (
    <div>
      {result.map((movie: any, idx: any) => {
        const link = movie.characters;
        return id.moviesId == movie.episode_id ? (
          <div key={idx}>
            <Movie
              poster={movie.img}
              title={movie.title}
              crawl={movie.opening_crawl}
              date={movie.release_date}
              producer={movie.producer}
              director={movie.director}
              characters={movie.characters}
            />
            <div>{link}</div>
          </div>
        ) : null;
      })}
    </div>
  );
};

export default MovieDetail;
