import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { getAllMovies } from "../redux/movies/movieSlice";
import { useSelector } from "react-redux";
import "./Movie.css";

interface Movie {
  poster?: string;
  title?: string;
  crawl?: string;
  date?: string;
  id?: number;
  producer?: string;
  director?: string;
  characters?: [];
}

const Movie: React.FC<Movie> = ({
  poster,
  title,
  crawl,
  date,
  id,
  producer,
  director,
}) => {
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState<boolean>(false);
  const result = useSelector(getAllMovies);
  const [favorites, setToFavorites] = useState([]);

  const addToFavorites = (id) => {
    result.map((movie: any, idx: any) => {
      return id == movie.episode_id
        ? localStorage.setItem(
            "movie",
            JSON.stringify({
              poster: movie.img,
              title: movie.title,
              crawl: movie.crawl,
              date: movie.release_date,
            })
          )
        : null;
    });
  };

  return (
    <div className="movie__card">
      <img
        src={poster}
        alt="Movie Poster"
        onClick={() => {
          {
            setIsVisible(!isVisible);

            navigate(`/${id}`);
          }
        }}
      />
      <div className="movie__description">
        <p>{title}</p>
        <span>{date}</span>
        <p> {crawl}</p>
        <div className={producer ? "movie__details" : "movie__details__hidden"}>
          <>
            <span>Producer:</span>
            <p>{producer}</p>
            <span>Director:</span>
            <p>{director}</p>
          </>
        </div>
        <button className="add__button" onClick={addToFavorites}>
          Add to favorites
        </button>
      </div>
    </div>
  );
};

export default Movie;
