import { useState, useEffect } from "react";
import axios from "axios";
import { useSelector } from "react-redux";
import { getAllMovies } from "../redux/movies/movieSlice";
import { useParams } from "react-router-dom";
import Movie from "../components/Movie";

const MovieDetail = () => {
  const id = useParams();
  const result = useSelector(getAllMovies);
  const [movieCharacters, setMovieCharacters] = useState<object[]>([]);

  const fetchCharacters = (characters: any) => {
    characters?.map((character: any) => {
      console.log(character);
      axios.get(character).then((response) => {
        const { name } = response.data;
        console.log(name);
        setMovieCharacters([...movieCharacters, name]);
      });
    });
    fetchCharacters(characters);
  };

  useEffect(() => {
    // axios.get("https://swapi.dev/api/people/4/").then((response) => {
    //   const { name } = response.data;
    //   console.log(name);
    //   setMovieCharacters([...movieCharacters, name]);
    // });
  }, []);

  console.log(movieCharacters);
  return (
    <div>
      {result.map((movie: any, idx: any) => {
        return id.moviesId == movie.episode_id ? (
          <div key={idx}>
            <Movie
              poster={movie.img}
              title={movie.title}
              crawl={movie.opening_crawl}
              date={movie.release_date}
              producer={movie.producer}
              director={movie.director}
              characters={movie.characters}
            />
          </div>
        ) : null;
      })}
    </div>
  );
};

export default MovieDetail;
