import { useNavigate } from "react-router-dom";
import { useState } from "react";
import "./Movie.css";
import axios from "axios";

interface Movie {
  poster?: string;
  title?: string;
  crawl?: string;
  date?: string;
  id?: number;
  producer?: string;
  director?: string;
  characters?: [];
}

const Movie: React.FC<Movie> = ({
  poster,
  title,
  crawl,
  date,
  id,
  producer,
  director,
  characters,
}) => {
  const [isVisible, setIsVisible] = useState<boolean>(true);
  const navigate = useNavigate();

  return (
    <div className="movie__card">
      <img
        src={poster}
        alt="Movie Poster"
        onClick={() => {
          navigate(`/${id}`);
        }}
      />
      <div className="movie__description">
        <p>{title}</p>
        <span>{date}</span>
        <p> {crawl}</p>
        <div
          className={isVisible ? "movie__details" : "movie__details__hidden"}
        >
          <p>{producer}</p>
          <p>{director}</p>

          {characters?.map((character: any, idx: any) => {
            axios.get(character).then((response) => {
              console.log(response.name);
            });
          })}
        </div>
      </div>
    </div>
  );
};

export default Movie;
